import React, {useState} from 'react'
import {data} from '../data/data'
import './PickMovie.css'

const PickMovie = () => {
    const [picked, setPicked] = useState(false);
    const [randMovie, setRandMovie] = useState({})

    const pickButton = () => {
        const random = data[Math.floor(Math.random() * data.length)]
        setRandMovie(random)
        setPicked(true)
    }

    return (
        <div>
            <h1>Pick a Movie</h1>
            {picked && <Movie image={randMovie.img} genre={randMovie.genre} name={randMovie.name}/>}
            {!picked && <h1>Not Picked</h1>}

            <button onClick={pickButton}>Click to Pick</button>
        </div>
    )
}

export default PickMovie
